	public int bulbs(ArrayList<Integer> A) {
		int allOnCount = 0;
		int switchCount = 0;
		for (int i=0;i < A.size();i++)
		{
			if(A.get(i) == 1)
			{
				allOnCount++;
			}
			else
			{
				pressSwitch(A,i);
				switchCount++;
				
				  if(!A.contains(0)) 
				  {
				  return switchCount;
				  }
				 
				
			}
		}
		
		if(allOnCount == A.size())
				return 0;
		return switchCount;
	}

	private void pressSwitch(ArrayList<Integer> A, int index) {
		for(int i = index;i<A.size(); i++)
		{
			if(A.get(i)==0)
				A.set(i, 1);
			else if(A.get(i)==1)
				A.set(i, 0);
		}
		
	}
	
	 public int bulbsProblem(Integer[] A) {
	        int count =0;
	        for(int i=0;i<A.length;i++){
	            if((A[i]+count)%2==0){
	                count++;
	            }
	        }
	        return count;
	    }

