package scaler.assigments.homework;

public class Strings_Assignment_Q7 {

	public static void main(String args[]) {
		//new Strings_Assignment_Q7().longestPalindrome("aaaabaaa");
		String val = new Strings_Assignment_Q7().solve("");
		System.out.println(val);
	}

	public String solve(String A) {

		StringBuilder stb = new StringBuilder();
		stb.append(A);
		StringBuilder result =  new StringBuilder();
		for (int i = 0; i < stb.length(); i++) {
			if (stb.charAt(i) >= 'A' && stb.charAt(i) <= 'Z') {
				
			} else {
				if (stb.charAt(i) == 'a' || stb.charAt(i) == 'e' || stb.charAt(i) == 'i' || stb.charAt(i) == 'o'
						|| stb.charAt(i) == 'u') {
					result.append('#') ;
				}
				else
					result.append(stb.charAt(i)) ;
					

			}
		}
		return new String(result.toString() + result.toString());
	}

	public String longestPalindrome(String A) {

		int start = 0;
		int maxlen = 0;
		int n = A.length();
		for (int i = 0; i < n - maxlen / 2; i++) {
			int j = i;
			int k = i;
			while (k < n - 1 && A.charAt(k) == A.charAt(k + 1))
				k++;
			while (j > 0 && k < n - 1 && A.charAt(j - 1) == A.charAt(k + 1)) {
				j--;
				k++;
			}
			int length = k - j + 1;
			if (length > maxlen) {
				start = j;
				maxlen = length;
			}
		}
		return A.substring(start, start + maxlen);
	}
}